INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'ar_SY', '{WEB_TITLE} - شخصا ما لديه سؤال عن اعلانك', '<p>مرحبا{CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE})ترك لك رسالة خاصة بأعلانك <a href="{ITEM_URL}">{ITEM_TITLE}</a>:</p><p>{COMMENT}</p><p>تحياتنا,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'ar_SY', 'من فضلك اكد {WEB_TITLE} حسابك', '<p>مرحبا{USER_NAME},</p><p>برحاء تحقيق صحة تسجليك من خلال الضغط على الرابط التالى: {VALIDATION_LINK}</p><p>مع الشكر!</p><p>تحياتنا,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'ar_SY', '{WEB_TITLE} - تسجيل ناجح!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'ar_SY', 'أنظر ماذا أكتشفت فى {WEB_TITLE}', '<p>مرحبا {FRIEND_NAME},</p><p>صديقك {USER_NAME} يريد مشاركتك هذا الاعلان <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>الرساله:</p><p>{COMMENT}</p><p>تحياتنا,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'ar_SY', '{WEB_TITLE} - اعلانات جديدة in the last hour', '<p>مرحبا{USER_NAME},</p><p>إعلانجديد تم نشهرم فى أخر ساعة. القى نظره عليهم:</p><p>{ADS}</p><p>-------------</p><p>لإلغاء الاشتراك فى هذه التنبيهات مستقبلا برجاء الضغط على: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'ar_SY', '{WEB_TITLE} - اعلانات جديدة in the last day', '<p>مرحبا {USER_NAME},</p><p>أعلان جديد تم نشره فى اخر يوم. القى نظرة عليهم:</p><p>{ADS}</p><p>-------------</p><p>لإلغاء الاشتراك فى هذه التنبيهات مستقبلا برجاء الضغط على: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'ar_SY', '{WEB_TITLE} - اعلانات جديدة in the last week', '<p>مرحبا{USER_NAME},</p><p>العديد من الاعلانات تم نشرها خلال لاسبوع الماضى. لمشاهدتهم:</p><p>{ADS}</p><p>-------------</p><p>لإيقاف الاشتراك فى تنبيهات مماثلة ، برجاء الضغك على: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'ar_SY', '{WEB_TITLE} - اعلانات جديدة', '<p>مرحبا {USER_NAME},</p><p>اعلان جديد تم نشره, تفحصه!</p><p>{ADS}</p><p>-------------</p><p>لالغاء الاشتراك, انقر علي: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'ar_SY', '{WEB_TITLE} - تعليق جديد', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'ar_SY', '{WEB_TITLE} - تغيير اعدادات الاعلان {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'ar_SY', '{WEB_TITLE} - اكد اعلانك', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'ar_SY', '{WEB_TITLE} - اعلان جديد تم نشره', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'ar_SY', '{WEB_TITLE} - استرجع كلمة مرورك', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'ar_SY', '{WEB_TITLE} - انت طلبت تغيير البريد الالكترونى', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'ar_SY', '{WEB_TITLE} - من فضلك اكد تنبيهك', 'برجاء التحقق من صحة تسجيل التنبيه الخاص بكم عن طريق الضغط على الرابط التالى');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'ar_SY', '{WEB_TITLE} - تعليقك تم قبوله', '<p>مرحبا {COMMENT_AUTHOR},</p><p>تعليقك علي <a href="{ITEM_URL}">{ITEM_TITLE}</a> تم قبوله.</p><p>تحياتنا,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'ar_SY', '{WEB_TITLE} - اكد اعلانك', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'ar_SY', '{WEB_TITLE} - مستخدم جديد قام بالتسجيل', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'ar_SY', '{WEB_TITLE} - شخصا ما لديه سؤال لك', '<p>مرحبا {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) ترك لك رسالة:</p><p>{COMMENT}</p><p>تحياتنا,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'ar_SY', '{WEB_TITLE} - شخصا ما علق علي اعلانك', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'ar_SY', '{WEB_TITLE} - تم انشاء حساب مدير بنجاح!', '<p>مرحبا {ADMIN_NAME},</p><p>مدير  {WEB_LINK} انشأ حساب لك,</p><ul><li>اسم المرور: {USERNAME}</li><li>كلمة المرور: {PASSWORD}</li></ul><p>يمكنك الوصول للوحة الادارة من هنا {WEB_ADMIN_LINK}.</p><p>شكرا لك!</p><p>مع التحية,</p>');
