INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'ca_ES', '{WEB_TITLE} - Algú té una pregunta sobre el teu anunci', '<p>Hola, {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) t\'ha deixat un missatge sobre el teu anunci <a href="{ITEM_URL}">{ITEM_TITLE}</a>:</p><p>{COMMENT}</p><p>Salutacions,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'ca_ES', 'Si us plau, confirma el teu compte de {WEB_TITLE}', '<p>Hola, {USER_NAME}!</p><p>Si us plau, valida el teu registre fent clic a l\'enllaç següent: {VALIDATION_LINK}</p><p>Gràcies!</p><p>Salutacions,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'ca_ES', '{WEB_TITLE} - T\'has registrat correctament!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'ca_ES', 'Mira què he descobert a {WEB_TITLE}', '<p>Hola, {FRIEND_NAME}!</p><p>El teu amic {USER_NAME} vol compartir aquest anunci amb tu <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Missatge:</p><p>{COMMENT}</p><p>Salutacions,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'ca_ES', '{WEB_TITLE} - Nous anuncis in the last hour', '<p>Hola, {USER_NAME}!</p><p>S\'han publicat nous anuncis al llarg de l\'última hora. Dóna\'ls un cop d\'ull:</p><p>{ADS}</p><p>-------------</p><p>Per deixar de rebre aquests avisos, fes clic aquí:{UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'ca_ES', '{WEB_TITLE} - Nous anuncis in the last day', '<p>Hola, {USER_NAME}!</p><p>S\'han publicat nous anuncis al llarg d\'ahir. Dóna\'ls un cop d\'ull:</p><p>{ADS}</p><p>-------------</p><p>Per deixar de rebre aquests avisos, fes clic aquí:{UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'ca_ES', '{WEB_TITLE} - Nous anuncis in the last week', '<p>Hola, {USER_NAME}!</p><p>S\'han publicat nous anuncis al llarg d\'aquesta setmana. Dóna\'ls un cop d\'ull:</p><p>{ADS}</p><p>-------------</p><p>Per deixar de rebre aquests avisos, fes clic aquí: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'ca_ES', '{WEB_TITLE} - Nous anuncis', '<p>Hola, {USER_NAME}!</p><p>S\'ha publicat un nou anunci, dóna-li un cop d\'ull!</p><p>{ADS}</p><p>-------------</p><p>Per deixar de rebre aquests avisos, fes clic aquí: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'ca_ES', '{WEB_TITLE} - Nou comentari', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'ca_ES', '{WEB_TITLE} - Edita les opcions de l\'anunci {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'ca_ES', '{WEB_TITLE} - Confirma el teu anunci', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'ca_ES', '{WEB_TITLE} - S\'ha publicat un nou anunci', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'ca_ES', '{WEB_TITLE} - Recupera la teva contrasenya', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'ca_ES', '{WEB_TITLE} - Has sol·licitat un canvi d\'adreça de correu electrònic', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'ca_ES', '{WEB_TITLE} - Si us plau, confirma el teu avís', '<p>Hola, {USER_NAME}!</p><p>Si us plau, confirma el teu registre a les alertes fent clic a l\'enllaç següent: {VALIDATION_LINK}</p><p>Gràcies!</p><p>Salutacions,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'ca_ES', '{WEB_TITLE} - El teu comentari ha estat acceptat', '<p>Hola, {COMMENT_AUTHOR}!</p><p>El teu comentari a <a href="{ITEM_URL}">{ITEM_TITLE}</a> ha estat aprovat.</p><p>Salutacions,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'ca_ES', '{WEB_TITLE} - Confirma el teu anunci', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'ca_ES', '{WEB_TITLE} - Un nou usuari s\'ha registrat', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'ca_ES', '{WEB_TITLE} - Algú té una pregunta per a tu', '<p>Hola, {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) t\'ha deixat un missatge:</p><p>{COMMENT}</p><p>Salutacions,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'ca_ES', '{WEB_TITLE} - Algú ha comentat el teu anunci', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'ca_ES', '{WEB_TITLE} - Compte d\'administrador creat amb èxit!', '<p>Hola, {ADMIN_NAME}!</p><p>L\'administrador de {WEB_LINK} ha creat un compte per a tu,</p><ul><li>Nom d\'usuari: {USERNAME}</li><li>Contrasenya: {PASSWORD}</li></ul><p>Pots accedir al tauler d\'administració aquí {WEB_ADMIN_LINK}.</p><p>Gràcies!</p><p>Salutacions,</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (22, 'ca_ES', '{WEB_TITLE} - El teu anunci està a punt de caducar', '<p>Hola, {USER_NAME}! </p><p>El teu anunci <a href="{ITEM_URL}">{ITEM_TITLE}</a> està a punt de caducar a {WEB_LINK}.');
