INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'cs_CZ', '{WEB_TITLE} - Prosím potvrďte vaše upozornění', '<p>Dobrý den {USER_NAME},</p><p>prosím potvrďte registraci upozornění kliknutím na následující odkaz: {VALIDATION_LINK}</p><p>Děkujeme!</p><p>S pozdravem,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'cs_CZ', '{WEB_TITLE} - Váš komentář byl schválen', '<p> Ahoj {COMMENT_AUTHOR}, </p><p> Váš komentář k <a href="{ITEM_URL}"> {ITEM_TITLE} </a> byl schválen. </p><p> S pozdravem,</p><p> {WEB_LINK} </p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'cs_CZ', '{WEB_TITLE} - Potvrzení vaší nabídky', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'cs_CZ', '{WEB_TITLE} - Registroval se nový uživatel', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
