INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'da_DK', '{WEB_TITLE} - Nogen har et spørgsmål vedr. din annonce', '<p>Hej {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) sendte dig en besked vedrørende din annonce <a href="{ITEM_URL}">{ITEM_TITLE}</a>:</p><p>{COMMENT}</p><p>Med venlig hilsen,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'da_DK', 'Valider venligst din {WEB_TITLE} brugerprofil', '<p>Hej {USER_NAME},</p><p>Valider venligst din registrering ved at klikke på dette link: {VALIDATION_LINK}</p><p>Mange tak!</p><p>Med venlig hilsen,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'da_DK', '{WEB_TITLE} - Registrering lykkedes!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'da_DK', 'Se hvad jeg fandt på {WEB_TITLE}', '<p>Hej {FRIEND_NAME},</p><p>Din ven {USER_NAME} vil gerne dele denne annonce med dig <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Besked:</p><p>{COMMENT}</p><p>Med venlig hilsen,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'da_DK', '{WEB_TITLE} - Ny annonce in the last hour', '<p>Hej {USER_NAME},</p><p>Nye annoncer er blevet publiceret indenfor den seneste time. Tag et kig på dem:</p><p>{ADS}</p><p>-------------</p><p>For at afmelde denne annonceagent, kan du klikke på dette link: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'da_DK', '{WEB_TITLE} - Ny annonce in the last day', '<p>Hej {USER_NAME},</p><p>Nye annoncer er blevet publiceret indenfor det seneste døgn. Tag et kig på dem:</p><p>{ADS}</p><p>-------------</p><p>For at afmelde denne annonceagent, kan du klikke på dette link: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'da_DK', '{WEB_TITLE} - Ny annonce in the last week', '<p>Hej {USER_NAME},</p><p>Nye annoncer er blevet publiceret indenfor den seneste uge. Tag et kig på dem:</p><p>{ADS}</p><p>-------------</p><p>For at afmelde denne annonceagent, kan du klikke på dette link: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'da_DK', '{WEB_TITLE} - Ny annonce', '<p>Hej {USER_NAME},</p><p>En ny annonce er blevet publiceret, tjek den ud!</p><p>{ADS}</p><p>-------------</p><p>For at afmelde denne annonceagent, kan du klikke på dette link: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'da_DK', '{WEB_TITLE} - Ny kommentar', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'da_DK', '{WEB_TITLE} - Redigeringsmuligheder for annoncen {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'da_DK', '{WEB_TITLE} - Valider din annonce', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'da_DK', '{WEB_TITLE} - En ny annonce er publiceret', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'da_DK', '{WEB_TITLE} - Genfind din adgangskode', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'da_DK', '{WEB_TITLE} - Du har anmodet om at skifte email', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'da_DK', '{WEB_TITLE} - Valider venligst din annonceagent', '<p>Hej {USER_NAME},</p><p>Validaer venligst oprettelsen af din annonceagent ved at klikke på dette link: {VALIDATION_LINK}</p><p>Mange tak!</p><p>Med venlig hilsen,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'da_DK', '{WEB_TITLE} - Din kommentar er blevet godkendt', '<br /><p>Hej: {COMMENT_AUTHOR},</p><br /><br /><p>Din kommentar til annoncen: <br /><a href="{ITEM_URL}">{ITEM_TITLE}</a> er blevet godkendt af en Administrator.</p><br />- -<br /><br /><p>Med venlig hilsen... {WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'da_DK', '{WEB_TITLE} - Valider din annonce', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'da_DK', '{WEB_TITLE} - En ny bruger er registreret', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'da_DK', '{WEB_TITLE} - Nogen har et spørgsmål til dig', '<p>Hej {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) har sendt dig en besked:</p><p>{COMMENT}</p><p>Med venlig hilsen,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'da_DK', '{WEB_TITLE} - Nogen har kommenteret din annonce', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'da_DK', 'Fra {WEB_TITLE} - Korrekt oprettet Admin konto', '<br /><p>Hej: {ADMIN_NAME},</p><br /><br /><p>Administrator hos {WEB_LINK} har oprettet en Admin konto til dig, med:<br /></p><ul><li>Brugernavn: {USERNAME}</li><br /><li>PassWord: {PASSWORD}</li><br /></ul><p>Din adgang sker via via kontrolpanel,<br />LogIND her: {WEB_ADMIN_LINK}.</p><br />- -<br /><br /><p>Med venlig hilsen... {WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (22, 'da_DK', 'Fra {WEB_TITLE} - Din annonce er ved at udløbe', '<p>Hej: {USER_NAME},</p><br /<p>BLOT TIL ORIENTERING<br />Din annonce <a href="{ITEM_URL}">{ITEM_TITLE}</a> er ved at udløbe.</p><br />- -<br /><br /><p>Med venlig hilsen... {WEB_LINK}</p>');
