INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'de_DE', '{WEB_TITLE} - Jemand hat eine Frage zu Ihrem Angebot', '<p>Hallo {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) hat Ihnen eine Nachricht zu Ihrem Angebot <a href="{ITEM_URL}">{ITEM_TITLE}</a> geschickt:</p><p>{COMMENT}</p><p>Beste Grüße,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'de_DE', 'Bitte bestätigen Sie Ihren {WEB_TITLE} Account', '<p>Hallo {USER_NAME},</p><p>bitte bestätigen Sie Ihre Registrierung mit Klick auf folgenden Link: {VALIDATION_LINK}</p><p>Vielen Dank!</p><p>Beste Grüße,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'de_DE', '{WEB_TITLE} - Registrierung erfolgreich!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'de_DE', 'Schau, was ich auf {WEB_TITLE} entdeckt habe', '<p>Hallo {FRIEND_NAME},</p><p>Ihr Freund {USER_NAME} möchte Ihnen dieses Angebot empfehlen <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Nachricht:</p><p>{COMMENT}</p><p>Beste Grüße,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'de_DE', '{WEB_TITLE} - Neue Angebote in the last hour', '<p>Hallo {USER_NAME},</p><p>In der letzten Stunde wurden neue Angebote veröffentlicht. Schauen Sie sich diese hier an:</p><p>{ADS}</p><p>-------------</p><p>Um diese Benachrichtigung abzubestellen, klicken Sie bitte hier: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'de_DE', '{WEB_TITLE} - Neue Angebote in the last day', '<p>Hallo {USER_NAME},</p><p>in den letzten 24 Stunden wurden neue Angebote veröffentlicht. Schauen Sie sich diese hier an:</p><p>{ADS}</p><p>-------------</p><p>Um diese Benachrichtigung abzubestellen, klicken Sie bitte hier: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'de_DE', '{WEB_TITLE} - Neue Angebote in the last week', '<p>Hallo {USER_NAME},</p><p>es wurden neue Angebote in der letzten Woche veröffentlicht. Hier können Sie sich diese anschauen:</p><p>{ADS}</p><p>-------------</p><p>Um diese Benachrichtigung abzubestellen, klicken Sie bitte hier: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'de_DE', '{WEB_TITLE} - Neue Angebote', '<p>Hallo {USER_NAME},</p><p>Ein neues Angebot wurde veröffentlicht. Schau es Dir an!</p><p>{ADS}</p><p>-------------</p><p>Um diese Benachrichtigung abzubestellen, klicken Sie bitte hier: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'de_DE', '{WEB_TITLE} - Neuer Kommentar', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'de_DE', '{WEB_TITLE} - Optionen für das Angebot bearbeiten {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'de_DE', '{WEB_TITLE} - Bestätigen Sie Ihre Anzeige', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'de_DE', '{WEB_TITLE} - Eine neue Anzeige wurde veröffentlicht', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'de_DE', '{WEB_TITLE} - Passwortwiederherstellung', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'de_DE', '{WEB_TITLE} - Sie haben die Änderung Ihrer E-Mailadresse beantragt', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'de_DE', '{WEB_TITLE} - Bitte bestätigen Sie Ihr Abonnement zu einer Benachrichtigung', '<p>Hallo {USER_NAME},</p><p>Bitte bestätigen Sie das Abonnement für Ihre Benachrichtigung indem Sie auf folgenden Link klicken: {VALIDATION_LINK}</p><p>Vielen Dank!</p><p>Beste Grüße,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'de_DE', '{WEB_TITLE} - Ihr Kommentar wurde freigegeben', '<p>Hallo {COMMENT_AUTHOR},</p><p>Ihr Kommentar zu <a href="{ITEM_URL}">{ITEM_TITLE}</a> wurde freigegeben.</p><p>Beste Grüße,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'de_DE', '{WEB_TITLE} - Bestätigen Sie Ihre Anzeige', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'de_DE', '{WEB_TITLE} - Ein neuer Benutzer hat sich angemeldet', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'de_DE', '{WEB_TITLE} - Jemand hat eine Frage an Sie', '<p>Hallo {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) hat Ihnen eine Nachricht hinterlassen:</p><p>{COMMENT}</p><p>Beste Grüße,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'de_DE', '{WEB_TITLE} - Jemand hat einen Kommentar zu Ihrem Angebot hinterlassen', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'de_DE', '{WEB_TITLE} - Admin-Konto erfolgreich erstellt!', '<p>Sehr geehrte/r Herr/Frau {ADMIN_NAME},</p><p>Der Administrator von {WEB_LINK} hat ein Benutzerkonto für Sie angelegt:</p><ul><li>Benutzername: {USERNAME}</li><li>Passwort: {PASSWORD}</li></ul><p>Sie können das Admin-Panel hier aufrufen: {WEB_ADMIN_LINK}.</p><p>Vielen Dank!!</p><p>Mit freundlichen Grüßen,</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (22, 'de_DE', '{WEB_TITLE} - Ihre Anzeige läuft demnächst ab', '<p>Hallo {USER_NAME},</p><p>Ihre Anzeige <a href="{ITEM_URL}">{ITEM_TITLE}</a> is dabei bald abzulaufen: {WEB_LINK}.');
