INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'el_GR', '{WEB_TITLE} - Κάποιος χρήστης έχει μία ερώτηση, σχετικά με τη καταχώρηση σας', '<p>Γειά σου {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) άφησε ένα μήνυμα σχετικά με τη καταχώρηση σας <a href="{ITEM_URL}">{ITEM_TITLE}</a>:</p><p>{COMMENT}</p><p>Φιλικοί χαιρετισμοί,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'el_GR', 'Παρακαλώ επικυρώστε τον  {WEB_TITLE} λογαριασμό', '<p>Γειά σου {USER_NAME},</p><p>Παρακαλώ επικυρώστε την εγγραφή σας, πατώντας στον ακόλουθο ηλεκτρονικό σύνδεσμο: {VALIDATION_LINK}</p><p>Ευχαριστούμε πολύ!</p><p>Φιλικοί χαιρετισμοί,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'el_GR', '{WEB_TITLE} - Επιτυχής εγγραφή!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'el_GR', 'Κοίτα τι ανακάλυψα στο {WEB_TITLE}', '<p>Γειά σου {FRIEND_NAME},</p><p>Ο φίλος σας {USER_NAME} θέλει να μοιραστεί αυτή τη καταχώρηση με εσάς <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Μήνυμα:</p><p>{COMMENT}</p><p>Φιλικοί χαιρετισμοί,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'el_GR', '{WEB_TITLE} - Νέες καταχωρήσεις in the last hour', '<p>Γειά σου {USER_NAME},</p><p>Νέες καταχωρήσεις έχουν δημοσιευτεί την προηγούμενη ώρα. Μπορείτε να τις δείτε εδώ:</p><p>{ADS}</p><p>-------------</p><p>Για να διαγραφείτε απο αυτή την ειδοποίηση, πατήστε εδώ: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'el_GR', '{WEB_TITLE} - Νέες καταχωρήσεις in the last day', '<p>Γειά σου {USER_NAME},</p><p>Νέες καταχωρήσεις έχουν δημοσιευτεί την προηγούμενη ημέρα. Μπορείτε να τις δείτε εδώ:</p><p>{ADS}</p><p>-------------</p><p>Για να διαγραφείτε απο αυτή την ειδοποίηση, πατήστε εδώ: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'el_GR', '{WEB_TITLE} - Νέες καταχωρήσεις in the last week', '<p>Γειά σου {USER_NAME},</p><p>Νέες καταχωρήσεις έχουν δημοσιευτεί την προηγούμενη εβδομάδα. Μπορείτε να τις δείτε εδώ:</p><p>{ADS}</p><p>-------------</p><p>Για να διαγραφείτε απο αυτή την ειδοποίηση, πατήστε εδώ: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'el_GR', '{WEB_TITLE} - Νέες καταχωρήσεις', '<p>Γειά σου{USER_NAME},</p><p>Μία νέα καταχώρηση δημοσιεύτηκε, ελέγξτε το!</p><p>{ADS}</p><p>-------------</p><p>Για να διαγραφείτε απο αυτή την ειδοποίηση, πατήστε εδώ: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'el_GR', '{WEB_TITLE} - Νέο σχόλιο', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'el_GR', '{WEB_TITLE} - Διαχείριση επιλογών για τη καταχώρηση {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'el_GR', '{WEB_TITLE} - Επικυρώστε την ειδοποίηση', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'el_GR', '{WEB_TITLE} - Μία νέα καταχώσηση δημοσιεύτηκε', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'el_GR', '{WEB_TITLE} - Ανακτήστε τον κωδικό πρόσβασης σας. ', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'el_GR', '{WEB_TITLE} - Ζητήσατε αλλαγή e-mail.', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'el_GR', '{WEB_TITLE} - Παρακαλώ καταχωρήστε την ειδοποίηση σας. ', '<p>Γειά σου {USER_NAME},</p><p>Παρακαλώ καταχωρείστε την ειδοποίηση σας, πατώντας στο παρακάτω σύνδεσμο: {VALIDATION_LINK}</p><p>Ευχαριστούμε πολύ!</p><p>Φιλικοί χαιρετισμοί,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'el_GR', '{WEB_TITLE} - Το σχόλιο σας, εγκρίθηκε', '<p>Γειά σου {COMMENT_AUTHOR},</p><p>Το σχόλιό σας στο <a href="{ITEM_URL}">{ITEM_TITLE}</a> έχει εγκριθεί.</p><p>Με εκτίμηση,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'el_GR', '{WEB_TITLE} - Επικυρώστε την ειδοποίηση', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'el_GR', '{WEB_TITLE} - Ένας νέος χρήστης εγγράφηκε', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'el_GR', '{WEB_TITLE} - Κάποιος χρήστης έχει μία ερώτηση για εσάς', '<p>Γειά σου{CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) άφησε ένα μήνυμα:</p><p>{COMMENT}</p><p>Φιλικοί χαιρετισμοί,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'el_GR', '{WEB_TITLE} - Κάποιος χρήστης σχολίασε τη καταχώρηση σας', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'el_GR', '{WEB_TITLE} - Επιτυχής δημιουργία λογαριασμού διαχειριστή!', '<p>Γειά σου {ADMIN_NAME},</p><p>Ο διαχειριστής του {WEB_LINK} έχει δημιουργήσει ένα λογαριασμό για εσένα με ,</p><ul><li>Όνομα χρήστη: {USERNAME}</li><li>Κωδικός: {PASSWORD}</li></ul><p>Μπορείς να εισέλθεις στο κέντρο διαχείρισης εδώ {WEB_ADMIN_LINK}.</p><p>Ευχαριστούμε πολύ!</p><p>Φιλικοί χαιρετισμοί,</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (22, 'el_GR', '{WEB_TITLE} - Η διαφήμιση σας, είναι έτοιμη να λήξει.', '<p>Γειά σου {USER_NAME},</p><p>Η καταχώρηση σας <a href="{ITEM_URL}">{ITEM_TITLE}</a> θα λήξει στις {WEB_LINK}.');
