INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'es_ES', '{WEB_TITLE} - Alguien tiene una pregunta acerca de tu anuncio', '<p>¡Hola {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) te ha dejado un mensaje acerca de tu anuncio <a href="{ITEM_URL}">{ITEM_TITLE}</a>:</p><p>{COMMENT}</p><p>Saludos,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'es_ES', 'Por favor, valida tu cuenta en {WEB_TITLE}', '<p>Hola {USER_NAME},</p><p>Por favor, valida tu registro haciendo clic en el siguiente enlace:  {VALIDATION_LINK}</p><p>¡Gracias!</p><p>Saludos,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'es_ES', '{WEB_TITLE} - ¡Te has registrado correctamente!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'es_ES', '¡Mira lo que he descubierto en {WEB_TITLE}!', '<p>Hola {FRIEND_NAME},</p><p>Tu amigo {USER_NAME} quiere compartir este anuncio contigo <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Mensaje: </p><p>{COMMENT}</p><p>Saludos,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'es_ES', '{WEB_TITLE} - Nuevos anuncios in the last hour', '<p>Hola {USER_NAME},</p><p>Se han publicado nuevos anuncios en la última hora. Échales un vistazo:</p><p>{ADS}</p><p>-------------</p><p>Para dejar de recibir esta alerta, haz clic en:  {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'es_ES', '{WEB_TITLE} - Nuevos anuncios in the last day', '<p>Hola {USER_NAME},</p><p>Se han publicados anuncios nuevos durante el día de hoy. Échales un vistazo: </p><p>{ADS}</p><p>-------------</p><p>Para dejar de recibir esta alerta, haz clic en: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'es_ES', '{WEB_TITLE} - Nuevos anuncios in the last week', '<p>Hola {USER_NAME},</p><p>Se han publicado nuevos anuncios en la última semana. Échales un vistazo: </p><p>{ADS}</p><p>-------------</p><p>Para dejar de recibir esta alerta, haz clic en:  {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'es_ES', '{WEB_TITLE} - Nuevos anuncios', '<p>Hola {USER_NAME},</p><p>Se ha publicado un nuevo anuncio, ¡échale un vistazo!</p><p>{ADS}</p><p>-------------</p><p>Para dejar de recibir esta alerta, haz clic en: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'es_ES', '{WEB_TITLE} - Nuevo comentario', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'es_ES', '{WEB_TITLE} - Opciones de edición del anuncio {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'es_ES', '{WEB_TITLE} - Valida tu anuncio', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'es_ES', '{WEB_TITLE} - Se ha publicado un anuncio nuevo', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'es_ES', '{WEB_TITLE} - Recuperar contraseña', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'es_ES', '{WEB_TITLE} - Has solicitado un cambio de correo electrónico', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'es_ES', '{WEB_TITLE} - Por favor, confirma tu alerta', '<p>Hola {USER_NAME},</p><p>Por favor, valida tu registro de alerta. Haz clic en el siguiente enlace: {VALIDATION_LINK}</p><p>¡Gracias!</p><p>Saludos,</p><p>{WEB_LINK}</p> ');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'es_ES', '{WEB_TITLE} - Tu comentario ha sido aprovado', '<p>Hola {COMMENT_AUTHOR},</p><p>Tu comentario en <a href="{ITEM_URL}">{ITEM_TITLE}</a> ha sido aprobado. </p><p>Saludos,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'es_ES', '{WEB_TITLE} - Valida tu anuncio', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'es_ES', '{WEB_TITLE} - Se ha registrado un nuevo usuario', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'es_ES', '{WEB_TITLE} - Alguien tiene una pregunta para ti', '<p>Hola {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) te ha dejado un mensaje: </p><p>{COMMENT}</p><p>Saludos,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'es_ES', '{WEB_TITLE} - Alguien ha comentado tu anuncio', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'es_ES', '{WEB_TITLE} - ¡Cuenta de administrador creada con éxito!', '<p>Hola {ADMIN_NAME},</p><p>El administrador de {WEB_LINK} ha creado una cuenta para ti,</p><ul><li>Nombre de usuario: {USERNAME}</li><li>Contraseña: {PASSWORD}</li></ul><p>Puedes acceder al panel de administración aquí {WEB_ADMIN_LINK}.</p><p>¡Gracias!</p><p>Saludos,</p>');
