INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'hr_HR', '{WEB_TITLE} - Potvrdite vaš oglas', '<p>Dobar dan {USER_NAME},</p><p>Dobili ste ovaj e-mail jer je oglas objavljen na {WEB_LINK}. Molimo potvrdite da želite objaviti oglas sljedeći ovu poveznicu: {VALIDATION_LINK}. Ako vi niste objavili ovaj oglas molimo da zanemarite ovaj e-mail.</p><p>Detalji oglasa:</p><p>Ime kontakta: {USER_NAME}<br />Kontakt e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Sa štovanjem,</p><p>{WEB_LINK}</p>');
