INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'hr_HR', '{WEB_TITLE} - Obnovite lozinku', '<p>Dobar dan {USER_NAME},</p><p>Šaljemo vam ovaj e-mail jer ste zatražili obnovu lozinke. Pratite sljedeću poveznicu da obnovite lozinku: {PASSWORD_LINK}</p><p>Poveznica važi 24 sata i nakon toga bit će obrisana.</p><p>Ako niste zatražili obnovu lozinke, molimo da zanemarite ovaj e-mail. Zahtjev za obnovu došao je sa ove IP adrese {IP_ADDRESS} u {DATE_TIME}</p><p>Sa štovanjem,</p><p>{WEB_LINK}</p>');
