INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'hu_HU', 'Kérjük erősítse meg {WEB_TITLE} fiókját', '<p>Tisztelt {USER_NAME},</p><p>Kérjük erősítse meg regisztrációját a következő linkre kattintva: {VALIDATION_LINK}</p><p>Köszönjük!</p><p>Üdvözlettel,</p><p>{WEB_LINK}</p>');
