INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'it_IT', 'Attiva la tua registrazione su {WEB_TITLE} ', '<p>Ciao {USER_NAME},</p><p>Attiva la tua registrazione cliccando sul seguente link: {VALIDATION_LINK}</p><p>Grazie!</p><p>Cordiali Saluti,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'it_IT', '{WEB_TITLE} - Registrazione avvenuta con successo!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'it_IT', 'Guarda che ho trovato su {WEB_TITLE}', '<p>Ciao {FRIEND_NAME},</p><p>Un tuo amico {USER_NAME} vuole segnalarti un annucio che potrebbe interessarti <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Messaggio:</p><p>{COMMENT}</p><p>Cordiali Saluti,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'it_IT', '{WEB_TITLE} - Nuovi annunci in the last hour', '<p>Ciao {USER_NAME},</p><p>Ecco gli annunci pubblicato nell'ultima ora. Dacci uno sguardo:</p><p>{ADS}</p><p>-------------</p><p>Per cancellare il promemoria, clicca sul seguente link: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
