INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'it_IT', '{WEB_TITLE} - Successo creazione di account admin!', '<p>Ciao {ADMIN_NAME},</p><p>L'amministratore di {WEB_LINK} ha creato un account per te,</p><ul><li>Utente: {USERNAME}</li><li>Password: {PASSWORD}</li></ul><p>Puoi accedere al pannello di amministrazione da qui: {WEB_ADMIN_LINK}.</p><p>Grazie!</p><p>Cordiali Saluti,</p> ');
