INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'ja_JA', 'あなたの{WEB_TITLE} アカウントの認証をしてください。', '<p>こんにちわ、 {USER_NAME}さん。</p><p>こちらのリンクをクリックしてアカウントを認証してください: {VALIDATION_LINK}</p><p>ありがとうございました!</p><p>よろしくお願いします。</p><p>{WEB_LINK}</p>');
