INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'ja_JA', '{WEB_TITLE} - 管理者アカウントを作成しました！', '<p>こんにちわ、{ADMIN_NAME}、</p><p>{WEB_LINK}の管理者があなた用のアカウントを作成しました。</p><ul><li>ユーザー名: {USERNAME}</li><li>パスワード: {PASSWORD}</li></ul><p>管理パネルへのアクセスはこちらから。{WEB_ADMIN_LINK}.</p><p>ありがとうございました!</p><p>よろしくお願いします。</p>');
