INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'lt_LT', '{WEB_TITLE} - Lankytojas susidomėjo jūsų sklelbimu', '<p>Labas {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) paliko jums žinutė dėl jūsų skelbimo <a href="{ITEM_URL}">{ITEM_TITLE}</a>:</p><p>{COMMENT}</p><p>Pagarbiai,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'lt_LT', 'Prašome aktyvuoti jūsų {WEB_TITLE} paskyrą', '<p>Labas {USER_NAME},</p><p>Prašome aktyvuoti registraciją, paspausdami ant šios nuorodos: {VALIDATION_LINK}</p><p>Ačiū!</p><p>Pagarbiai</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'lt_LT', '{WEB_TITLE} - Registracija pavyko!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'lt_LT', 'Aš noriu būti surastas per {WEB_TITLE}', '<p>Labas {FRIEND_NAME},</p><p>Jūsų draugas {USER_NAME} nori pasidalinti skelbimo nuoroda <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Žinutė:</p><p>{COMMENT}</p><p>Pagarbiai,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'lt_LT', '{WEB_TITLE} - Nauji skelbimai in the last hour', '<p>Labas {USER_NAME},</p><p>paskutinės valandos naujausi skelbimai. Peržiūrėkite:</p><p>{ADS}</p><p>-------------</p><p>Atsisakyti pranešimų gali paspaudę šią nuorodą: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'lt_LT', '{WEB_TITLE} - Nauji skelbimai in the last day', '<p>Labas {USER_NAME},</p><p>Naujausi skelbimai buvo patalpinti per praėjusią dieną. Peržiūrėkite:</p><p>{ADS}</p><p>-------------</p><p>Atsisakyti pranešimų galite: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'lt_LT', '{WEB_TITLE} - Nauji skelbimai in the last week', '<p>Labas{USER_NAME},</p><p>Naujausi praeitos savaitės skelbimai. Peržiūrėkite:</p><p>{ADS}</p><p>-------------</p><p>Atsisakyti šių pranešimų: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'lt_LT', '{WEB_TITLE} - Nauji skelbimai', '<p>Labas {USER_NAME},</p><p>Naujas skelbimas patalpintas!</p><p>{ADS}</p><p>-------------</p><p>Atsisakyti tolimesnių pranešimų: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'lt_LT', '{WEB_TITLE} - Naujas komentaras', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'lt_LT', '{WEB_TITLE} - Pakeisti skelbimo pasirinkimus {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'lt_LT', '{WEB_TITLE} - Aktyvuokite savo sklebimą', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'lt_LT', '{WEB_TITLE} - Patalpintas naujas skelbimas', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'lt_LT', '{WEB_TITLE} - Pasikeiskite savo slaptažodį', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'lt_LT', '{WEB_TITLE} - Jūs prašėte pakeisti el. pašto adresą', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'lt_LT', '{WEB_TITLE} - Prašome patvirtinti pranešimą', '<p>Labas {USER_NAME},</p><p>Prašome patvirtini įspėjimą nuspausdami šią nuorodą: {VALIDATION_LINK}</p><p>Ačiū</p><p>Pagarbiai,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'lt_LT', '{WEB_TITLE} - Jūsų komentaras yra patvirtintas', '<p>Labas {COMMENT_AUTHOR},</p><p>Jūsų komentaras <a href="{ITEM_URL}">{ITEM_TITLE}</a> buvo patvirtintas.</p><p>Pagarbiai,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'lt_LT', '{WEB_TITLE} - Aktyvuokite savo sklebimą', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'lt_LT', '{WEB_TITLE} - Užsiregistravo naujas lankytojas', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'lt_LT', '{WEB_TITLE} - Lankytojas jums turi klausimą', '<p>Labas {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) paliko jums žinutę:</p><p>{COMMENT}</p><p>Pagarbiai</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'lt_LT', '{WEB_TITLE} - Lankytojas pakomentavo jūsų skelbimą', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'lt_LT', '{WEB_TITLE}  Administratoriaus prieiga sėkmingai sukurta!', '<p> Labas {ADMIN_NAME},</p><p> Administratorius {WEB_LINK} sukurtas</p><ul><li>Prisijungimo vardas:{USERNAME}</li><li>Slaptažodis:{PASSWORD}</li></ul><p>Prisijungit prie administravimo pulto galite čia{WEB_ADMIN_LINK}. </p><p> Ačiū!</p><p>Linkėjimai,</p>');
