INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'nl_NL', '{WEB_TITLE} - Iemand heeft een vraag over uw advertentie', '<p>Hallo {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) heeft een bericht achter gelaten over advertentie <a href="{ITEM_URL}">{ITEM_TITLE}</a>:</p><p>{COMMENT}</p><p>Vriendelijke groet,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'nl_NL', 'Valideer uw registratie bij {WEB_TITLE}', '<p>Hallo {USER_NAME},</p><p>Valideer uw registratie door op de volgende link te klikken: {VALIDATION_LINK}</p><p>Bedankt!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'nl_NL', '{WEB_TITLE} - Succesvol aangemeld!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'nl_NL', 'Kijk wat ik op gevonden heb op {WEB_TITLE}', '<p>Hallo {FRIEND_NAME},</p><p>{USER_NAME} wil de volgend eadvertentie graag met u delen <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Bericht:</p><p>{COMMENT}</p><p>Vriendelijke groet,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'nl_NL', '{WEB_TITLE} - Nieuwe advertenties in the last hour', '<p>Hallo {USER_NAME},</p><p>Nieuwe advertenties van het afgelopen uur. Klik hier om ze te bekijken:</p><p>{ADS}</p><p>-------------</p><p>Klik op de volgende link voor afmelding van deze alerts: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'nl_NL', '{WEB_TITLE} - Nieuwe advertenties in the last day', '<p>Hallo {USER_NAME},</p><p>Nieuwe advertenties van de afgelopen dag. Bekijk ze hier:</p><p>{ADS}</p><p>-------------</p><p>Klik op de volgende link om deze alerts niet meer te ontvangen: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'nl_NL', '{WEB_TITLE} - Nieuwe advertenties in the last week', '<p>Hallo {USER_NAME},</p><p>Nieuwe advertenties van de afgelopen week. Bekijk ze hier:</p><p>{ADS}</p><p>-------------</p><p>Klik op de volgende link indien u deze alerts niet meer wilt ontvangen: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'nl_NL', '{WEB_TITLE} - Nieuwe advertenties', '<p>Hallo {USER_NAME},</p><p>Er is een nieuwe advertentie gepubliceerd, bekijk de advertentie!</p><p>{ADS}</p><p>-------------</p><p>Klik op de volgende link als u deze alerts niet meer wilt ontvangen: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'nl_NL', '{WEB_TITLE} - Nieuwe reactie', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'nl_NL', '{WEB_TITLE} - Wijzig opties voor advertentie {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'nl_NL', '{WEB_TITLE} - Valideer uw advertentie', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'nl_NL', '{WEB_TITLE} - Er is een nieuwe advertentie gepubliceerd', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'nl_NL', '{WEB_TITLE} - Herstel uw wachtwoord', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'nl_NL', '{WEB_TITLE} - Verzoek wijziging emailadres', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'nl_NL', '{WEB_TITLE} - Valideer uw alert', '<p>Hallo {USER_NAME},</p><p>Valideer uw alert registratie door op de volgende link te klikken: {VALIDATION_LINK}</p><p>Bedankt!</p><p>Vriendelijke groet,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'nl_NL', '{WEB_TITLE} - Uw reactie is geaccordeerd', '<p>Hallo {COMMENT_AUTHOR},</p><p>Uw commentaar op  <a href="{ITEM_URL}">{ITEM_TITLE}</a> is goedgekeurd.</p><p>Groeten,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'nl_NL', '{WEB_TITLE} - Valideer uw advertentie', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'nl_NL', '{WEB_TITLE} - Er is een nieuwe gebruiker aangemeld', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'nl_NL', '{WEB_TITLE} - Iemand heeft een vraag', '<p>Hallo {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) heeft het volgende bericht achter gelaten:</p><p>{COMMENT}</p><p>Vriendelijke groet,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'nl_NL', '{WEB_TITLE} - Iemand heeft een reactie op uw advertentie', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'nl_NL', '{WEB_TITLE} - Succes creëren admin account!', '<p>Hallo {ADMIN_NAME},</p><p>De admin van {WEB_LINK} heeft met succes een account voor u,</p><ul><li>Gebruikersnaam: {USERNAME}</li><li>Wachtwoord:  {PASSWORD}</li></ul><p>U kunt nu gebruik maken van het admin panneel hier  {WEB_ADMIN_LINK}.</p><p>Hartelijk dank!!</p><p>gegroet,</p>');
