INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'pl_PL', 'Prosimy o potwierdzenie Twojego {WEB_TITLE} konta', '<p>Witaj {USER_NAME},</p><p>Potwierdź swoją rejestrację klikając na poniższy link: {VALIDATION_LINK}</p><p>Dziękujemy!</p><p>Pozdrowienia,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'pl_PL', '{WEB_TITLE} - Rejestracja przebiegła pomyślnie', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'pl_PL', 'Sprawdź to {WEB_TITLE}! Może Cię zainteresuje', '<p>Witaj {FRIEND_NAME},</p><p>Twój znajomy {USER_NAME} przesyła Ci ogłoszenie, które może Cię zainteresować<a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Wiadomość:</p><p>{COMMENT}</p><p>Pozdrawiam,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'pl_PL', '{WEB_TITLE} - Nowe ogłoszenie in the last hour', '<p>Witaj {USER_NAME},</p><p>W ostatniej godzinie opublikowano nowe ogłoszenia. Sprawdź je!</p><p>{ADS}</p><p>-------------</p><p> Jeśli nie chcesz otrzymywać tych wiadomości, kliknij ten link:{UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'pl_PL', '{WEB_TITLE} - Nowe ogłoszenie in the last day', '<p>Witaj {USER_NAME},</p><p>W ostatnim dniu opublikowano nowe ogłoszenia. Sprawdź je!</p><p>{ADS}</p><p>-------------</p><p> Jeśli nie chcesz otrzymywać tych wiadomości, kliknij ten link:{UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'pl_PL', '{WEB_TITLE} - Nowe ogłoszenie in the last week', '<p>Witaj {USER_NAME},</p><p>Nowe ogłoszenie opublikowane w ubiegłym tygodniu. Sprawdź je:</p><p>{ADS}</p><p>-------------</p><p> Jeśli nie chcesz otrzymywać tych wiadomości, kliknij ten link:: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'pl_PL', '{WEB_TITLE} - Nowe ogłoszenie', '<p>Witaj {USER_NAME},</p><p>Opublikowane nowe ogłoszenie, sprawdź je! </p><p>{ADS}</p><p>-------------</p><p>Jeśli nie chcesz otrzymywać tych wiadomości, kliknij ten link: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'pl_PL', '{WEB_TITLE} - Nowy komentarz', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'pl_PL', '{WEB_TITLE} - Edytuj opcje ogłoszeń {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'pl_PL', '{WEB_TITLE} - Potwierdzenie ogłoszenia', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'pl_PL', '{WEB_TITLE} - Opublikowano nowe ogłoszenie ', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'pl_PL', '{WEB_TITLE} - Odzyskiwanie hasła‏', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'pl_PL', '{WEB_TITLE} - Zmiana adresu e-mail', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'pl_PL', '{WEB_TITLE} – Prosimy o potwierdzenie powiadomienia', '<p>Witaj {USER_NAME},</p><p>Kliknij na podany link, aby otrzymywać powiadomienia: {VALIDATION_LINK}</p><p>Dziękujemy!</p><p>Pozdrowienia,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'pl_PL', '{WEB_TITLE} - Twój komentarz został zatwierdzony', '<p>Witaj {COMMENT_AUTHOR},</p><p>Twój komentarz dotyczący<a href="{ITEM_URL}">{ITEM_TITLE}</a>został zatwierdzony</p><p>Pozdrowienia,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'pl_PL', '{WEB_TITLE} - Potwierdzenie ogłoszenia', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'pl_PL', '{WEB_TITLE} – Zarejestrował się nowy użytkownik', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'pl_PL', '{WEB_TITLE} - Ktoś ma pytanie dotyczące Twojego ogłoszenia', '<p>Witaj {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) zostawił Ci wiadomość:</p><p>{COMMENT}</p><p>Pozdrowienia,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'pl_PL', '{WEB_TITLE} – Ktoś skomentował Twoje ogłoszenie', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
