INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'tr_TR', '{WEB_TITLE} - Yeni ilanlar in the last hour', '<p>Merhaba {USER_NAME},</p><p>Son bir saat içinde yeni ilanlar yayınlandı. İlanlara bir göz atın:</p><p>{ADS}</p><p>-------------</p><p>Bu uyarının aboneliğinden ayrılmak için burayı tıklayın: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'tr_TR', '{WEB_TITLE} - Yeni ilanlar in the last day', '<p>Merhaba {USER_NAME},</p><p>Son bir gün içinde yeni ilanlar yayınlandı. İlanlara bir göz atın:</p><p>{ADS}</p><p>-------------</p><p>Bu uyarının aboneliğinden ayrılmak için burayı tıklayın: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'tr_TR', '{WEB_TITLE} - Yeni ilanlar in the last week', '<p>Merhaba {USER_NAME},</p><p>Son bir hafta içinde yeni ilanlar yayınlandı. İlanlara bir göz atın:</p><p>{ADS}</p><p>-------------</p><p>Bu uyarının aboneliğinden ayrılmak için burayı tıklayın: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'tr_TR', '{WEB_TITLE} - Yeni ilanlar', '<p>Merhaba {USER_NAME},</p><p>Yeni bir ilan yayınlandı, bir göz atın !</p><p>{ADS}</p><p>-------------</p><p>Bu uyarının aboneliğinden ayrılmak için burayı tıklayın: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'tr_TR', '{WEB_TITLE} - Yeni yorum', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'tr_TR', '{WEB_TITLE} - {ITEM_TITLE} başlıklı ilan için seçenekleri düzenleyin', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'tr_TR', '{WEB_TITLE} - İlanınızı doğrulayın', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'tr_TR', '{WEB_TITLE} - Yeni bir ilan yayınlandı', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'tr_TR', '{WEB_TITLE} - Şifrenizi geri edinin', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'tr_TR', '{WEB_TITLE} - E-postanızın değiştirilmesini talep ettiniz', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'tr_TR', '{WEB_TITLE} - Lüten uyarıyı doğrulayın', '<p>Merhaba {USER_NAME},</p><p>Aşağıdaki linke tıklayarak uyarıya kaydınızı doğrulayın: {VALIDATION_LINK}</p><p>Teşekkürler!</p><p>Saygılar,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'tr_TR', '{WEB_TITLE} - Yorumunuz onaylandı', '<p>Merhaba {COMMENT_AUTHOR},</p><p><a href="{ITEM_URL}">{ITEM_TITLE}</a> başlıklı ilana yorumunuz onaylandı.</p><p>Saygılar,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'tr_TR', '{WEB_TITLE} - İlanınızı doğrulayın', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'tr_TR', '{WEB_TITLE} - Yeni bir kullanıcı kayıt oldu', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'tr_TR', '{WEB_TITLE} - Size bir soru iletildi', '<p>Merhaba {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) bir mesaj gönderdi:</p><p>{COMMENT}</p><p>Saygılar,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'tr_TR', '{WEB_TITLE} - İlanınıza ait bir yorum var', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'tr_TR', '{WEB_TITLE} - Yönetici hesabı başarıyla oluşturuldu!', '<p>Merhaba {ADMIN_NAME},</p><p>{WEB_LINK} sitesinin yöneticisi sizin için bir ehsap oluşturdu,</p><ul><li>Kullanıcı adınız: {USERNAME}</li><li>Şifreniz: {PASSWORD}</li></ul><p>Yönetici paneline buradan erişebilirsiniz: {WEB_ADMIN_LINK}.</p><p>Teşekkür ederiz!</p><p>Saygılar,</p>');
