INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'zh_TW', '請驗證你的 {WEB_TITLE} 帳號', '<p>{USER_NAME} 你好；</p><p>請驗證你的註冊是否為你本人，請按一下右側超連結：{VALIDATION_LINK}</p><p>謝謝你！</p><p>祝好</p><p>{WEB_LINK}</p>');
